mapper.select:
        SELECT


--         级联表的字段
        ,xianhua.xianhua_name as xianhuaName
        ,xianhua.xianhua_photo as xianhuaPhoto
        ,xianhua.xianhua_types as xianhuaTypes
        ,xianhua.xianhua_kucun_number as xianhuaKucunNumber
        ,xianhua.xianhua_price as xianhuaPrice
        ,xianhua.xianhua_old_money as xianhuaOldMoney
        ,xianhua.xianhua_new_money as xianhuaNewMoney
        ,xianhua.xianhua_clicknum as xianhuaClicknum
        ,xianhua.shangxia_types as shangxiaTypes
        ,xianhua.xianhua_delete as xianhuaDelete
        ,xianhua.xianhua_content as xianhuaContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_sum_jifen as yonghuSumJifen
        ,yonghu.yonghu_new_jifen as yonghuNewJifen
        ,yonghu.huiyuandengji_types as huiyuandengjiTypes

        FROM xianhua_commentback  a
        left JOIN xianhua xianhua ON a.xianhua_id = xianhua.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.xianhua_id = #{params.xianhuaId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.xianhua_commentback_text like CONCAT('%',#{params.xianhuaCommentbackText},'%')


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.xianhua_id IS NOT NULL


                and xianhua.xianhua_name like CONCAT('%',#{params.xianhuaName},'%')


                and xianhua.xianhua_types = #{params.xianhuaTypes}



                  and xianhua.xianhua_kucun_number >= #{params.xianhuaKucunNumberStart}   


                  and xianhua.xianhua_kucun_number <= #{params.xianhuaKucunNumberEnd}   


                and xianhua.xianhua_kucun_number = #{params.xianhuaKucunNumber}


                  and xianhua.xianhua_price >= #{params.xianhuaPriceStart}   


                  and xianhua.xianhua_price <= #{params.xianhuaPriceEnd}   


                and xianhua.xianhua_price = #{params.xianhuaPrice}


                  and xianhua.xianhua_old_money >= #{params.xianhuaOldMoneyStart}   


                  and xianhua.xianhua_old_money <= #{params.xianhuaOldMoneyEnd}   


                  and xianhua.xianhua_new_money >= #{params.xianhuaNewMoneyStart}   


                  and xianhua.xianhua_new_money <= #{params.xianhuaNewMoneyEnd}   


                  and xianhua.xianhua_clicknum >= #{params.xianhuaClicknumStart}   


                  and xianhua.xianhua_clicknum <= #{params.xianhuaClicknumEnd}   


                and xianhua.xianhua_clicknum = #{params.xianhuaClicknum}


                and xianhua.shangxia_types = #{params.shangxiaTypes}



                  and xianhua.xianhua_delete >= #{params.xianhuaDeleteStart}   


                  and xianhua.xianhua_delete <= #{params.xianhuaDeleteEnd}   


                and xianhua.xianhua_delete = #{params.xianhuaDelete}


                and xianhua.xianhua_content like CONCAT('%',#{params.xianhuaContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                  and yonghu.yonghu_sum_jifen >= #{params.yonghuSumJifenStart}   


                  and yonghu.yonghu_sum_jifen <= #{params.yonghuSumJifenEnd}   


                  and yonghu.yonghu_new_jifen >= #{params.yonghuNewJifenStart}   


                  and yonghu.yonghu_new_jifen <= #{params.yonghuNewJifenEnd}   


                and yonghu.huiyuandengji_types = #{params.huiyuandengjiTypes}




        order by a.${params.orderBy} desc
